/*Table structure for table `itkee_category` */

DROP TABLE IF EXISTS `itkee_category`;

CREATE TABLE `itkee_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '分类ID',
  `name` varchar(50) NOT NULL COMMENT '分类名称',
  `namemark` varchar(50) DEFAULT NULL COMMENT '别名',
  `en_namemark` varchar(255) DEFAULT NULL COMMENT '英文名称',
  `alias` varchar(50) DEFAULT '' COMMENT '证书编号',
  `score` varchar(50) DEFAULT '2' COMMENT '每题分数',
  `pass_score` varchar(50) DEFAULT '60' COMMENT '及格分数',
  `time` varchar(50) DEFAULT '' COMMENT '时间',
  `sort` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `pid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上级分类ID',
  `path` varchar(255) DEFAULT '' COMMENT '路径',
  `create_time` varchar(50) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=25 DEFAULT CHARSET=utf8 COMMENT='分类表';

